select.if:
SELECT t.TABLE_NAME AS tableName, c.COMMENTS AS comment FROM user_tables t, user_tab_comments c WHERE t.table_name = c.table_name and t.TABLE_NAME not in (select a.table_name from online_table a)
AND t.TABLE_NAME = #{tableName}
ORDER BY t.TABLE_NAME

SELECT t.name AS tableName,b.value AS comments FROM sys.objects t LEFT JOIN sys.extended_properties b ON b.major_id=t.object_id and b.minor_id=0 and b.class=1 AND b.name='MS_Description' WHERE t.type='U' and t.name not in (select a.table_name from online_table a)
AND t.tableName = #{tableName}
ORDER BY t.name

SELECT t.COLUMN_NAME AS "dbFieldName", (CASE WHEN t.DATA_LENGTH IS NULL THEN t.DATA_PRECISION ELSE t.DATA_LENGTH END) as dbFieldLength, t.DATA_SCALE as dbFieldDecimal, (CASE WHEN t.NULLABLE = 'Y' THEN '1' ELSE '0' END) AS "nullable", (t.COLUMN_ID) AS sort, c.COMMENTS AS dbFieldDesc, t.DATA_TYPE AS dbFieldType FROM user_tab_columns t, user_col_comments c WHERE t.TABLE_NAME = c.table_name AND t.COLUMN_NAME = c.column_name
AND t.TABLE_NAME = #{name}
ORDER BY t.COLUMN_ID

SELECT t.COLUMN_NAME AS dbFieldName, (CASE WHEN t.IS_NULLABLE = 'YES' THEN '1' ELSE '0' END) AS "nullable", (t.ORDINAL_POSITION) AS sort, isnull(g.[value], '') AS dbFieldDesc, t.DATA_TYPE as dbFieldType, (CASE WHEN t.DATA_TYPE IN ('varchar','char','nvarchar','nchar') THEN t.CHARACTER_MAXIMUM_LENGTH WHEN t.DATA_TYPE IN ('numeric','decimal') THEN t.NUMERIC_PRECISION_RADIX ELSE 0 END) AS dbFieldLength, ISNULL(t.NUMERIC_SCALE, 0) AS dbFieldDecimal FROM INFORMATION_SCHEMA.COLUMNS t INNER JOIN sys.sysobjects o ON t.TABLE_NAME=o.name AND SCHEMA_NAME(o.uid)=t.TABLE_SCHEMA LEFT JOIN sys.extended_properties g ON o.id = g.major_id AND t.ORDINAL_POSITION = g.minor_id AND g.name='MS_Description' WHERE t.TABLE_SCHEMA = (SCHEMA_NAME())
AND t.TABLE_NAME = #{name}
ORDER BY t.ORDINAL_POSITION

SELECT t.COLUMN_NAME AS dbFieldName, (CASE WHEN t.IS_NULLABLE = 'YES' THEN 1 ELSE 0 END) AS nullable, (t.ORDINAL_POSITION) AS sort, t.COLUMN_COMMENT AS dbFieldDesc, (CASE WHEN t.CHARACTER_MAXIMUM_LENGTH > 0 THEN t.CHARACTER_MAXIMUM_LENGTH ELSE t.NUMERIC_PRECISION END) AS dbFieldLength, t.NUMERIC_SCALE AS dbFieldDecimal, t.DATA_TYPE AS dbFieldType, (CASE WHEN t.COLUMN_KEY = 'PRI' THEN 1 ELSE 0 END) AS primaryKey FROM information_schema.`COLUMNS` t WHERE t.TABLE_SCHEMA = (select database())
AND t.TABLE_NAME = #{name}
ORDER BY t.ORDINAL_POSITION
