mapper.select:
select

from sys_dict a


        and a.dict_code like CONCAT('%',  #{dictCode}, '%')


        and a.dict_code =  #{dictCode}


        and a.dict_name like  CONCAT('%', #{dictName}, '%')


        and a.type = #{type}


        and a.category = #{category}




        order by ${orderBy}


        order by a.sort asc
