mapper.select:
select

from sys_user a


        and a.name like CONCAT('%', #{name}, '%')


        and a.account like CONCAT('%', #{account}, '%')


        and a.status = #{status}


        and a.phone like CONCAT('%', #{phone}, '%')


        and a.gender = #{gender}




        ${orderBY}


        order by a.create_time asc
